package laba;
import java.util.Scanner;
public class scanner1 {

	public static void main(String[] args) {
		Scanner input=new Scanner(System.in);
		//reading a integer
		int x;
		x=input.nextInt();
		//reading float
		float f;
		f=input.nextFloat();
		System.out.println("x="+x);
		System.out.println("f="+f);
		input.close();

		// TODO Auto-generated method stub

	}

}
